import React from 'react';
import ReactDOM from 'react-dom';
import Web3 from 'web3';
import M, { updateTextFields } from 'materialize-css';
import './index.css';
import Identicon from 'identicon.js';

class PriceAndInfoSideDiv extends React.Component {
    render() {
        return (
            <div class="divPrice z-depth-5">
                Price: <br />
                0.1 ETH/FESB <br />
                10 FESB/ETH <br /><br />

                Pool info:<br />
                10 FESB <br />
                1 ETH
            </div>
        );
    }
}

class Icon extends React.Component {
    render() {
        return (
            <div class="row center-align">
                <i class="material-icons">{this.props.arrow}</i>
            </div>
        );
