class NavBar extends React.Component {

    constructor(props) {
        super(props);

        this.handleSwapClick = this.handleSwapClick.bind(this);
        this.handlePoolClick = this.handlePoolClick.bind(this);
        this.handleConnect = this.handleConnect.bind(this);
        this.disconnect = this.disconnect.bind(this);

        this.state = ({
            pool: "",
            swap: "active",
            detectedAccount: false
        })

        const navbar = document.querySelector('.swapnpool');
        var instance = M.Sidenav.init(navbar, {});
    }

    handleSwapClick(e) {
        e.preventDefault();
        this.props.swiping(true)
        this.setState({
            pool: "",
            swap: this.props.active,
        })
    }

    handlePoolClick(e) {
        e.preventDefault();
        this.props.swiping(false)
        this.setState({
            pool: this.props.active,
            swap: ""
        })
    }

    async handleConnect() {
        if (typeof window.ethereum !== 'undefined') {
            await ethereum.request({ method: 'eth_requestAccounts' });
            this.setState({
                detectedAccount: true
            })
        } else {
            console.log("please install metamask")
        }
    }

    //NE RADI
    disconnect() {
        this.setState({
            detectedAccount: false
        })
    }

    async getChain() {
        const chainId = await ethereum.request({ method: 'eth_chainId' });
        let chainName = null;
        if(chainId === 1)
        chainName = 'Mainnet'
        else if(chainId === 3)
        chainName = 'Ropsten'
        else if(chainId === 4)
        chainName = 'Rinkeby'
        else if(chainId === 42)
        chainName = 'Kovan'
        else console.log('None detected')
    }

    async componentDidMount() {

        setTimeout(async () => {
            const accounts = await ethereum.request({ method: 'eth_accounts' });
            this.setState({
                detectedAccount: accounts ? accounts[0] : false,
            })
        }, 1000);
    }

    render() {

        ethereum.on('accountsChanged', this.handleConnect)
        ethereum.on('disconnect', this.disconnect);

        let options = {
            foreground: [0, 0, 0, 255],               // rgba black
            background: [255, 255, 255, 255],         // rgba white
            margin: 0.2,                              // 20% margin
            size: 420,                                // 420px square
            format: 'svg'                             // use SVG instead of PNG
        };

        return (
            <div>
                <nav>
                    <div class="nav-wrapper pink lighten-3">
                        <a href="#" class="brand-logo center">MatejSwap</a>
                        <ul id="nav-mobile" class="left hide-on-med-and-down swapnpool">
                            <li onClick={this.handleSwapClick} class={this.state.swap}><a href="">Swap</a></li>
                            <li onClick={this.handlePoolClick} class={this.state.pool}><a href="">Pool</a></li>
                        </ul>
                        <ul class="right hide-on-med-and-down">
                            {this.state.detectedAccount ? <div><li>{ethereum.selectedAddress} </li><li><img width="40" height="40" src={`data:image/svg+xml;base64,${new Identicon(ethereum.selectedAddress, options).toString()}`} /></li></div> : <li onClick={this.handleConnect}><a class="waves-effect waves-light btn">Connect Wallet <i class="material-icons right">account_balance_wallet</i></a></li>}
                        </ul>
                    </div>
                </nav>
            </div>
        );
    }
}

