import Color from 'color';
import MersenneTwister from 'mersenne-twister';

const WOBBLE = 30;

const BASE_COLORS = [
  '#01888C', // teal
  '#FC7500', // bright orange
  '#034F5D', // dark teal
  '#F73F01', // orangered
  '#FC1960', // magenta
  '#C7144C', // raspberry
  '#F3C100', // goldenrod
  '#1598F2', // lightning blue
  '#2465E1', // sail blue
  '#F19E02', // gold
].map(hex => Color(hex));

export class Colors {
  readonly colors: Color[];

  constructor(
    private readonly generator: MersenneTwister,
    private readonly wobble: number = WOBBLE
  ) {
    // Hue Shift
    const amount = generator.random() * 30 - this.wobble / 2;
    this.colors = BASE_COLORS.map(color => {
      color.rotate(amount);
      return color;
    });
  }

  next() {
    this.generator.random(); // Apparently, to maintain compatibility with original Jazzicon
    const idx = Math.floor(this.colors.length * this.generator.random());
    return this.colors.splice(idx, 1)[0];
  }
}
import React from 'react';
import Color from 'color';

export const BASE_STYLES = {
  borderRadius: '50%',
  overflow: 'hidden',
  padding: 0,
  margin: 0,
  width: '100%',
  height: '100%',
  display: 'inline-block',
};

export interface PaperProps {
  color: Color;
  className?: string;
}

export function Paper(props: React.PropsWithChildren<PaperProps>) {
  const style = Object.assign({}, BASE_STYLES, {
    backgroundColor: props.color.hex(),
  });

  return (
    <div className={props.className}>
      <div style={style}>{props.children}</div>
    </div>
  );
}
export const SVG_NS = 'http://www.w3.org/2000/svg';
import React from 'react';
import MersenneTwister from 'mersenne-twister';
import { Colors } from './colors';
import { SVG_NS } from './svg.ns';

export function Shape(props: {
  generator: MersenneTwister;
  total: number;
  index: number;
  colors: Colors;
}) {
  const diameter = 100;
  const firstRot = props.generator.random();
  const angle = Math.PI * 2 * firstRot;
  const velocity =
    (diameter / props.total) * props.generator.random() +
    (props.index * diameter) / props.total;

  const tx = Math.cos(angle) * velocity;
  const ty = Math.sin(angle) * velocity;

  const translate = 'translate(' + tx.toFixed(3) + ' ' + ty.toFixed(3) + ')';

  const secondRot = props.generator.random();
  const rot = firstRot * 360 + secondRot * 180;
  const center = diameter / 2;
  const rotate = 'rotate(' + rot.toFixed(1) + ' ' + center + ' ' + center + ')';
  const transform = translate + ' ' + rotate;
  const fill = props.colors.next();

  return (
    <rect
      xmlns={SVG_NS}
      x={0}
      y={0}
      width={'100%'}
      height={'100%'}
      transform={transform}
      fill={fill.hex()}
    />
  );
}
import React from 'react';
import MersenneTwister from 'mersenne-twister';
import { Colors } from './colors';
import { Paper } from './paper.component';
import { Shape } from './shape.component';
import { SVG_NS } from './svg.ns';

const DEFAULT_SHAPE_COUNT = 3;

export interface JazziconProps {
  address: string;
  className?: string;
}

function times(n: number): number[] {
  return Array.from({ length: n }, (_, i) => i);
}

export function Jazzicon(props: JazziconProps) {
  const seed = parseInt(props.address.toLowerCase().slice(2, 10), 16);
  const generator = new MersenneTwister(seed);
  const colors = new Colors(generator);
  const paperColor = colors.next();

  const renderShapes = () => {
    return times(DEFAULT_SHAPE_COUNT).map(i => {
      return (
        <Shape
          generator={generator}
          total={DEFAULT_SHAPE_COUNT}
          index={i}
          key={`shape-${i}`}
          colors={colors}
        />
      );
    });
  };

  return (
    <Paper color={paperColor} className={props.className}>
      <svg x={0} y={0} xmlns={SVG_NS} viewBox={'0 0 100 100'}>
        {renderShapes()}
      </svg>
    </Paper>
  );
}
