import React from 'react';
import ReactDOM from 'react-dom';
import M, { updateTextFields } from 'materialize-css';
import './index.css';

class Icon extends React.Component {
    render() {
        return (
            <div class="row center-align">
                <i class="material-icons">{this.props.arrow}</i>
            </div>
        );
    }
}

class Button extends React.Component {
    render() {
        return (
            <div class="row center-align marpad">
                <a class="waves-effect waves-light btn-large col s12">{this.props.func}</a>
            </div>
        );
    }
}

class InputField extends React.Component {
    render() {
        return (
            <div class="row">
                <div class="col s12">This is where you input the desired ETH amount</div>
                <div class="col s10">
                    <form>
                        <div class="input-field">
                            <input placeholder="0.0" type="number" min="0" step=".01" class="validate" />
                            <label for="value">ETH</label>
                        </div>
                    </form>
                </div>

                <div class="col s2"><img src={this.props.imgsrc} class="fill" alt="Italian Trulli" /> </div>
            </div>
        );
    }
}

class InputFieldTwo extends React.Component {
    render() {
        return (
            <div class="row">
                <div class="col s12">This is where you input the desired FESB amount</div>
                <div class="col s10">
                    <form>
                        <div class="input-field">
                            <input placeholder="0.0" type="number" min="0" step=".01" class="validate" />
                            <label for="value">FESB</label>
                        </div>
                    </form>
                </div>

                <div class="col s2"><img src={this.props.imgsrc} class="fill" alt="Italian Trulli" /> </div>
            </div>
        );
    }
}

class Tab extends React.Component {
    render() {
        return (
            <li onClick={this.props.onClick} class="tab col s4"><a href="#test1">{this.props.name}</a></li>
        );
    }
}

class Container extends React.Component {

    constructor(props) {
        super(props);
        this.state = ({
            arrow: 'arrow_downward',
            disabled: 0
        });

        this.handleBuyClick = this.handleBuyClick.bind(this);
        this.handleSellClick = this.handleSellClick.bind(this);
        this.handlePoolClick = this.handlePoolClick.bind(this);

        const tabs = document.querySelector('.tabs');
        var instance = M.Tabs.init(tabs, {});
    }

    handleBuyClick() {
        this.setState({
            arrow: 'arrow_downward',
            disabled: 0
        })
    }

    handleSellClick() {
        this.setState({
            arrow: 'arrow_upward',
            disabled: 1
        })
    }

    handlePoolClick() {
        this.setState({
            arrow: 'import_export',
            disabled: null
        })
    }

    render() {
        return (
            <div class="row div z-depth-5">
                <div class="col s12">
                    <ul class="tabs">
                        <Tab name="Buy" onClick={this.handleBuyClick} />
                        <Tab name="Sell" onClick={this.handleSellClick} />
                        <Tab name="Pool" onClick={this.handlePoolClick} />
                    </ul>
                    <div class="hr" />
                </div>
                <div class="marpad">
                    <InputField />
                    <Icon arrow={this.state.arrow} />
                    <InputFieldTwo />
                    <Button func={ (this.state.arrow === 'arrow_downward') ? 'Buy' : (this.state.arrow === 'arrow_upward') ? 'Sell' : 'Provide Liquidity'}/>
                </div>
            </div>
        );
    }
}

class NavBar extends React.Component {
    render() {
        return (
            <div>
                <nav>
                    <div class="nav-wrapper pink lighten-3">
                        <a href="#" class="brand-logo center">MatejSwap</a>
                        <ul id="nav-mobile" class="right hide-on-med-and-down">
                        </ul>
                    </div>
                </nav>
            </div>
        );
    }
}

class Page extends React.Component {
    render() {
        return (
            <div>
                <NavBar />
                <Container />
            </div>
        );
    }
}

ReactDOM.render(<Page />, document.getElementById("root"));
