      network: chainName,
      detectedAccount: accounts ? accounts[0] : false
    });
  }

  async handleConnect() {
    if (window.ethereum) {
      const accounts = await ethereum.request({
        method: 'eth_requestAccounts'
      });
      const chainId = await ethereum.request({
        method: 'eth_chainId'
      });
      let chainName = null;
      if (chainId === '0x1') chainName = 'Mainnet';else if (chainId === '0x3') chainName = 'Ropsten';else if (chainId === '0x4') chainName = 'Rinkeby';else if (chainId === '0x2a') chainName = 'Kovan';else console.log('None detected');
      this.setState({
        network: chainName,
        detectedAccount: accounts ? accounts[0] : false
      });
      this.loadWalletInfo();
    } else {
      window.open('https://metamask.io', '_blank');
    }
  }

  async handleChange() {
    if (window.ethereum) {
      await this.getBlockchain();
      await this.loadWalletInfo();
    } else {}
  }

  async componentDidMount() {
    if (this.state.alertDialog) {
      ethereum.on('accountsChanged', this.handleChange);
      ethereum.on('chainChanged', this.handleChange);
      setTimeout(async () => {
        await this.getBlockchain();
        await this.loadWalletInfo();
      }, 1000);
    } else {}
  }

