import React from 'react';
import ReactDOM from 'react-dom';
import M, { updateTextFields } from 'materialize-css';
import './index.css';

class PriceAndInfoSideDiv extends React.Component {
    render() {
        return (
            <div class="divPrice z-depth-5">
                Price: <br />
                0.1 ETH/FESB <br />
                10 FESB/ETH <br /><br />

                Pool info:<br />
                10 FESB <br />
                1 ETH
            </div>
        );
    }
}

class Icon extends React.Component {
    render() {
        return (
            <div class="row center-align">
                <i class="material-icons">{this.props.arrow}</i>
            </div>
        );
    }
}

class Button extends React.Component {
    render() {
        return (
            <div class="row center-align marpad">
                <a class="waves-effect waves-light btn-large col s12">{this.props.func}</a>
            </div>
        );
    }
}

class InputField extends React.Component {
    render() {
        return (
            <div class="row">
                <div class="col s12 font">{this.props.text}</div>
                <div class="col s10">
                    <form>
                        <div class="input-field">
                            <input disabled={this.props.disabled} placeholder="0.0" type="number" min="0" step=".01" class="validate" />
                            <label for="value">Balance of ETH: </label>
                        </div>
                    </form>
                </div>

                <div class="col s2"><img src="eth.png" class="fill" alt="Italian Trulli" /> </div>
            </div>
        );
    }
}

class InputFieldTwo extends React.Component {
    render() {
        return (
            <div class="row">
                <div class="col s12 font">{this.props.text}</div>
                <div class="col s10">
                    <form>
                        <div class="input-field">
                            <input disabled={this.props.disabled} placeholder="0.0" type="number" min="0" step=".01" class="validate" />
                            <label for="value">Balance of FESB: </label>
                        </div>
                    </form>
                </div>

                <div class="col s2"><img src="fesb.jpg" class="fill" alt="Italian Trulli" /> </div>
            </div>
        );
    }
}

class Tab extends React.Component {
    render() {
        return (
            <li onClick={this.props.onClick} class="tab col s6"><a href="#test1">{this.props.name}</a></li>
        );
    }
}

class Container extends React.Component {

    constructor(props) {
        super(props);
        this.state = ({
            arrow: 'arrow_downward',
            disabledETH: '',
            disabledFESB: 'disabled',
            textDisplayETH: 'This is where you enter your ETH amount: ',
            textDisplayFESB: 'This is how much FESB coins you get: '
        });

        this.handleBuyClick = this.handleBuyClick.bind(this);
        this.handleSellClick = this.handleSellClick.bind(this);

        const tabs = document.querySelector('.tabs');
        var instance = M.Tabs.init(tabs, {});
    }

    handleBuyClick() {
        this.setState({
            arrow: 'arrow_downward',
            disabledETH: '',
            disabledFESB: 'disabled',
            textDisplayETH: 'This is where you enter your ETH amount: ',
            textDisplayFESB: 'This is how much FESB coins you get: '
        })
    }

    handleSellClick() {
        this.setState({
            arrow: 'arrow_upward',
            disabledETH: 'disabled',
            disabledFESB: '',
            textDisplayETH: 'This is how much ETH you get: ',
            textDisplayFESB: 'This is where you enter your FESB amount: '
        })
    }

    /* handlePoolClick() {
        this.setState({
            arrow: 'import_export',
            disabledETH: '',
            disabledFESB: '',
            textDisplayETH: 'Enter amount of ETH to provide to the liquidity pool: ',
            textDisplayFESB: 'Enter amount of FESB to provide to the liquidity pool: '
        })
    } */

    render() {
        return (
            <div class="div z-depth-5">
                <div class="col s12">
                    <ul class="tabs">
                        <Tab name="Buy" onClick={this.handleBuyClick} />
                        <Tab name="Sell" onClick={this.handleSellClick} />
                    </ul>
                    <div class="hr" />
                </div>
                <div class="marpad">
                    <InputField disabled={this.state.disabledETH} text={this.state.textDisplayETH} />
                    <Icon arrow={this.state.arrow} />
                    <InputFieldTwo disabled={this.state.disabledFESB} text={this.state.textDisplayFESB} />
                    <Button func={(this.state.arrow === 'arrow_downward') ? 'Buy' : 'Sell'} />
                </div>
            </div>
        );
    }
}

class NavBar extends React.Component {

    constructor(props) {
        super(props);
        this.state = ({
            pool: "",
            swap: "active"
        })

    this.handleSwapClick = this.handleSwapClick.bind(this);
    this.handlePoolClick = this.handlePoolClick.bind(this);

    const navbar = document.querySelector('.swapnpool');
    var instance = M.Tabs.init(navbar, {});

    }

    handleSwapClick() {
        this.setState({
            pool: "active",
            swap: ""
        })
    }

    handlePoolClick() {
        this.setState({
            pool: "active",
            swap: ""
        })
    }

    render() {
        return (
            <div>
                <nav>
                    <div class="nav-wrapper pink lighten-3">
                        <a href="#" class="brand-logo center">MatejSwap</a>
                        <ul id="nav-mobile" class="left hide-on-med-and-down swapnpool">
                            <li class={this.state.swap}><a href="">Swap</a></li>
                            <li class={this.state.pool}><a href="">Pool</a></li>
                        </ul>
                        <ul class="right hide-on-med-and-down">
                            <li><a class="waves-effect waves-light btn">Connect Wallet <i class="material-icons right">account_balance_wallet</i></a></li>
                        </ul>
                    </div>
                </nav>
            </div>
        );
    }
}

class Page extends React.Component {
    render() {
        return (
            <div class="row">
                <div>
                    <NavBar />
                </div>
                <div class="col s3">
                    <PriceAndInfoSideDiv />
                </div>
                <div class="col s6">
                    <Container />
                </div>
                <div class="col s3">Ovdi bi tribale bit upute</div>
            </div>
        );
    }
}

ReactDOM.render(<Page />, document.getElementById("root"));
