import React from 'react';
import ReactDOM from 'react-dom';
import M, { updateTextFields } from 'materialize-css';
import './index.css';

class Icon extends React.Component {
    render() {
        return (
            <div class="center-align">
                <i class="material-icons">{this.props.arrow}</i>
            </div>
        );
    }
}

class Button extends React.Component {
    render() {
        return (
            <div class="row center-align marpad">
                <a class="waves-effect waves-light btn-large col s12">Buy / Sell / Provide Liq</a>
            </div>
        );
    }
}

class InputField extends React.Component {
    render() {
        return (
            <div class="row">
                <div class="col s12">This is where you input the desired {this.props.crypto} amount</div>
                <div class="col s9">9-columns</div>
                <div class="col s3"><img src={this.props.imgsrc} class="fill" alt="Italian Trulli" /> </div>
            </div>
        );
    }
}

class Tab extends React.Component {
    render() {
        return (
            <li id={this.props.id} onClick={this.props.onClick(this.props.onClick)} class="tab col s4"><a href="#test1">{this.props.name}</a></li>
        );
    }
}

class Container extends React.Component {

    constructor(props) {
        super(props);
        this.state = ({
            arrow: 'arrow_downward'
        });

        this.handleBuyClick = this.handleBuyClick.bind(this);
        this.handleSellClick = this.handleSellClick.bind(this);

        const tabs = document.querySelector('.tabs');
        var instance = M.Tabs.init(tabs, {});
    }

    handleBuyClick() {
        this.setState({
            arrow: 'arrow_downward'
        })
    }

    handleSellClick() {
        this.setState({
            arrow: 'arrow_upward'
        })
        console.log('govno iz nosa');
    }

    render() {
        return (
            <div class="row div z-depth-5">
                <div class="col s12">
                    <ul class="tabs">
                        <Tab id={0} name="Buy" onClick={this.handleBuyClick}/>
                        <Tab id={1} name="Sell" />
                        <Tab id={2} name="Pool" />
                    </ul>
                    <div class="hr" />
                </div>
                <div class="marpad">
                    <InputField crypto="ETH" imgsrc="eth.png" />
                    <Icon arrow={this.state.arrow} />
                    <InputField crypto="FESB" imgsrc="fesb.jpg" />
                </div>
                <div>
                    <Button />
                </div>
            </div>
        );
    }
}

class NavBar extends React.Component {
    render() {
        return (
            <div>
                <nav>
                    <div class="nav-wrapper pink lighten-3">
                        <a href="#" class="brand-logo center">MatejSwap</a>
                        <ul id="nav-mobile" class="right hide-on-med-and-down">
                        </ul>
                    </div>
                </nav>
            </div>
        );
    }
}

class Page extends React.Component {
    render() {
        return (
            <div>
                <NavBar />
                <Container />
            </div>
        );
    }
}

ReactDOM.render(<Page />, document.getElementById("root"));
